INSERT INTO `gtwebsite_professional` (`id`, `name`, `group`, `login_id`, `password`, `position`, `introduce`, `state`, `head_ico`, `work_time`, `work_place`, `email`, `sort`) VALUES
(20, '戴耀华', 2, '06', '43573390b53be6d608346f1b8480562c', '医护师', '<p class="MsoNormal" style="line-height:200%;"><span style="line-height:200%;font-family:宋体;font-size:14px;">&nbsp; &nbsp; 中国儿童卫生保健疾病防治指导中心主任，世界卫生组织儿童卫生合作中心主任，中华预防医学会儿童保健分会副主任委员，首都儿研所儿童保健研究室主任</span></p>\r\n<p class="MsoNormal" style="line-height:200%;"><span style="mso-bidi-font-size:10.5pt;line-height:200%;font-family:宋体;mso-ascii-font-family:" times="" new="" roman";mso-hansi-font-family:"times="" roman""=""><br />\r\n</span></p>', 1, 'views/default/skin/default/images/front/expert_ico.gif', '', '杭州', 'a@ximalaya.com', 5),
(14, '葛可佑', 1, '01', 'e10adc3949ba59abbe56e057f20f883e', '教授　(营养学专家)', '<p style="line-height:200%;text-indent:21pt;mso-char-indent-count:2.0;" class="MsoNormal"><span style="font-family:宋体;font-size:14px;">中国营养学会名誉理事长。中国学生营养促进会副会长及专家委员会主任委员。</span></p>\r\n<p style="line-height:200%;text-indent:21pt;mso-char-indent-count:2.0;" class="MsoNormal"><span style="font-size:14px;" lang="EN-US">1958</span><span style="font-family:宋体;font-size:14px;">年毕业于北京医学院。</span><span style="font-size:14px;" lang="EN-US">80</span><span style="font-family:宋体;font-size:14px;">年代曾在</span><span style="font-size:14px;" lang="EN-US">FAO</span><span style="font-family:宋体;font-size:14px;">总部</span><span style="font-size:14px;" lang="EN-US">(</span><span style="font-family:宋体;font-size:14px;">罗马</span><span style="font-size:14px;" lang="EN-US">)</span><span style="font-family:宋体;font-size:14px;">担任食物营养高级官员。</span><span style="font-size:14px;" lang="EN-US">1990-1998</span><span style="font-family:宋体;font-size:14px;">年任中国预防医学科学院营养与食品卫生研究所研究员、所长。</span><span style="font-size:14px;" lang="EN-US"> </span></p>\r\n<p style="line-height:200%;text-indent:21pt;mso-char-indent-count:2.0;" class="MsoNormal"><span style="font-family:宋体;font-size:14px;">近年来，他主要从事人群营养状况的调查和改善研究，领导中国营养学会修订了中国居民膳食指南，创作了</span><span style="font-size:14px;" lang="EN-US">"</span><span style="font-family:宋体;font-size:14px;">平衡膳食宝塔</span><span style="font-size:14px;" lang="EN-US">"</span><span style="font-family:宋体;font-size:14px;">，开展了广泛的群众性营养教育工作，组织全国知名营养专家修定了我国的</span><span style="font-size:14px;" lang="EN-US">RDA</span><span style="font-family:宋体;font-size:14px;">，发表了我国第一部膳食营养素参考摄入量（</span><span style="font-size:14px;" lang="EN-US">DRIs</span><span style="font-family:宋体;font-size:14px;">）。获</span><span style="font-size:14px;" lang="EN-US">1987</span><span style="font-family:宋体;font-size:14px;">年国家科技进步奖二等奖。并于</span><span style="font-size:14px;" lang="EN-US">1992</span><span style="font-family:宋体;font-size:14px;">年经卫生部批准为有突出贡献的中青年专家，享受政府特殊津贴。多年来发表研究论文及专著等</span><span style="font-size:14px;" lang="EN-US">110</span><span style="font-family:宋体;font-size:14px;">余篇部，其</span><span style="font-size:14px;" lang="EN-US">40</span><span style="font-family:宋体;font-size:14px;">余篇在国外刊物上发表或载入国际学术会议论文文集，在国内外均有较深影响。在营养科学研究领域作出了杰出贡献。</span></p>\r\n<p style="line-height:200%;text-indent:21pt;mso-char-indent-count:2.0;" class="MsoNormal"><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14px" roman??="" ?times="" roman?;mso-hansi-font-family:="" new="" times=""><br />\r\n</span></p>', 1, 'upload/2012/01/13/20120113063951421.jpg', '', '杭州', 'test@test.com', 1),
(16, '黄献军', 1, '03', 'e10adc3949ba59abbe56e057f20f883e', '营养学专家', '<p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">1988</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 第一军医大学军医系毕业；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">1988-1993</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 任普通外科医师；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2000</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 创立第一家临床营养公司；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2005</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 获广东省科技成果三等奖（临床营养）；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2008</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 创办广州纽健生物科技有限公司；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2009</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 参与卫生部《临床营养科建设与管理指南（试行）》出台；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2009</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 首创“临床营养科运营服务专家”系统和概念；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2010</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 参编卫生部《临床诊疗指南<span lang="EN-US" style="font-size:14px;">-</span>临床营养科分册（试行）》；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2010</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 参与和推动临床营养国家“<span lang="EN-US" style="font-size:14px;">863</span>重大项目”申报；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2011</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年 首创“临床营养科绩效系统”，完善“临床营养科运营服务专家”七大项目，加速推动全国临床营养学科的建设。<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="font-size:14.0pt;line-height:200%;font-family:宋体;"><o:p><span style="font-size:14px;">&nbsp;</span></o:p></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">1</span><span style="line-height:200%;font-family:宋体;font-size:14px;">、十一年来亲身走访过全国<span lang="EN-US" style="font-size:14px;">20</span>个省<span lang="EN-US" style="font-size:14px;">400</span>余医院，指导临床营养科的建设和管理，帮助树立“以病人为中心”的营养治疗指导思想；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">2</span><span style="line-height:200%;font-family:宋体;font-size:14px;">、主编《临床营养通讯》，发送全国<span lang="EN-US" style="font-size:14px;">1000</span>余家医院；<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-family:宋体;font-size:14px;">3</span><span style="line-height:200%;font-family:宋体;font-size:14px;">、曾受邀在北京、上海、天津、武汉、郑州、长沙、昆明等地三甲医院，为当地医院营养师作《营养科现状与未来》、《推动<span lang="EN-US" style="font-size:14px;">NST</span>建设，发展肠内营养》、《医院营养师成长路径探讨》等主题演讲，反应热烈。<span lang="EN-US"><o:p></o:p></span></span></p>\r\n<span style="font-size:14px;"> </span><p class="MsoNormal" style="line-height:200%;"><span style="line-height:200%;font-family:宋体;font-size:14px;"><br />\r\n</span></p>', 1, 'upload/2012/01/13/20120113064553928.jpg', '', '杭州', 'a@ximalaya.com', 2),
(18, '于雨', 2, '05', 'e10adc3949ba59abbe56e057f20f883e', '主治医师', '<p><span style="font-family:宋体;font-size:14px;">主任</span><span style="font-size:14px;" lang="EN-US">&nbsp;&nbsp; </span><span style="font-family:宋体;font-size:14px;">主治医师</span><span style="font-size:14pt;" lang="EN-US"><!--?xml:namespace prefix = o /--><o:p></o:p></span></p>\r\n<p><span style="line-height:200%;font-size:14px;font-weight:normal;">毕业于解放军第三军医大学军医系，获医学学士学位。从事临床工作<span style="font-size:14px;" lang="EN-US">30</span>余年。曾任<span style="font-size:14px;" lang="EN-US">XX</span>军区第二届医学科技进步奖评奖委员会委员。对临床基础医学、妇产科和青春期生理卫生、新兴起的医疗产业健康体检及医学科学大众化普及教育方面有较深的研究，诊疗工作态度仔细全面、科学严谨。在国内刊物、杂志发表多篇论文。主编了医学科普丛书《别说你懂健康》，已由辽宁科技出版社出版发行。</span></p>\r\n<!--EndFragment--><br />', 1, 'upload/2012/01/13/20120113065104629.jpg', '', '网络', 'a@ximalaya.com', 2),
(22, '荫士安', 1, '07', 'b0218296b2364aa7c40a955cd8235a0d', '营养学专家', '<p class="MsoNormal" style="line-height:200%;"><span style="font-family:宋体;font-size:14px;">&nbsp; &nbsp; 中国预防医学科学院妇幼营养研究室主任，中国营养学会妇幼营养分会副主任委员。</span><span lang="EN-US" style="font-size:14px;">1998</span><span style="font-family:宋体;font-size:14px;">年获国家特殊津贴。主要研究孕妇、乳母的营养代谢和特殊营养需要的研究，有关儿童营养性疾病的诊断、病因和防治研究，孕妇、乳母和儿童保健食品的开发以及配方的研制。</span></p>\r\n<p class="MsoNormal" style="line-height:200%;"><span style="font-family:宋体;font-size:12px;"><br />\r\n</span></p>', 1, 'views/default/skin/default/images/front/expert_ico.gif', '', '杭州', 'a@ximalaya.com', 3),
(36, '谷立民', 4, '02', 'e10adc3949ba59abbe56e057f20f883e', '国家二级心理咨询师', '<p style="margin:0cm;margin-bottom:.0001pt;text-indent:17.85pt;line-height:200%;"><span lang="EN-US" style="line-height:200%;font-size:14px;">2003</span><span style="line-height:200%;font-size:14px;">年开始从事心理咨询工作；参加编写由国家级出版社出版的大学生心理健康教育用书，担当中等职业学校《心理健康教育》课本的副主编，曾任青岛晚报《家庭成长》栏目主持人、科普大学家庭教育方面外聘讲师。</span><span lang="EN-US" style="line-height:200%;font-size:14px;">&nbsp;</span><span lang="EN-US" style="font-size:13.5pt;line-height:200%;"><o:p></o:p></span></p><p style="margin:0cm;margin-bottom:.0001pt;text-indent:17.85pt;line-height:200%;"><span style="line-height:200%;font-size:14px;">执著于非疾病性心理健康工作；不是着眼于补救、纠正，而是着眼于成长；在接待家长和学生的来访中，积累了丰富的个案经验，尤其擅长个体成长、学习方法、亲子沟通和人际交往问题的咨询与辅导。</span><span lang="EN-US" style="font-size:13.5pt;line-height:200%;"><o:p></o:p></span></p>', 1, 'upload/2012/01/14/20120114043423481.jpg', '', '家里', 'a@ximalaya.com', 1),
(39, '林枫', 1, '009', 'c8c605999f3d8352d7bb792cf3fdb25b', '二级营养师', '<p class="MsoNormal" style="line-height:200%;"><span style="line-height:200%;font-family:宋体;font-size:14px;">从事儿科护理专业</span><span lang="EN-US" style="line-height:200%;font-size:14px;">12</span><span style="line-height:200%;font-family:宋体;font-size:14px;">余年，中国首批二级营养师，长期潜心研究公共营养问题，具有丰富的理论和实践经验。</span></p>', 1, 'upload/2012/01/16/20120116025746230.png', '', '家中', 'a@ximalaya.com', 9),
(43, '黄丽红', 1, '33', '2b16598ffae5b25e016c5c30721b30d2', '二级营养师', '<p><span style="font-size:14px;">&nbsp;</span><span class="apple-converted-space" style="line-height:200%;"><span lang="EN-US" style="line-height:200%;font-size:14px;">&nbsp;</span></span><span lang="EN-US" style="line-height:200%;font-size:14px;">&nbsp;2005</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年</span><span lang="EN-US" style="line-height:200%;font-size:14px;">11</span><span style="line-height:200%;font-family:宋体;font-size:14px;">月</span><span lang="EN-US" style="line-height:200%;font-size:14px;">~2006</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年</span><span lang="EN-US" style="line-height:200%;font-size:14px;">6</span><span style="line-height:200%;font-family:宋体;font-size:14px;">月在南京医科大学参加中国营养学会举办的公共营养师培训班，参加全国公共营养师考试取得二级营养师证书。</span><span lang="EN-US" style="line-height:200%;font-size:14px;">2006 ~2008</span><span style="line-height:200%;font-family:宋体;font-size:14px;">年就职于南京明基医院，</span><span lang="EN-US" style="line-height:200%;font-size:14px;">2008 ~2011</span><span style="line-height:200%;font-family:宋体;font-size:14px;">就职于鼓楼医院。擅长糖尿病的饮食调理和健康教育，省级刊物上发表过《糖尿病患者可选择升糖指数低的水果》等文章。在明基医院和鼓楼医院担任糖尿病健教护士期间，曾多次对糖尿病的营养方面调理进行授课培训，受到医生护士和病人的很大好评。</span></p>', 1, 'upload/2012/02/13/20120213010140160.gif', '', '网络', 'a@ximalaya.com', 8),
(53, '江志雄', 2, '999', 'c8c605999f3d8352d7bb792cf3fdb25b', '副主任医师', '<p style="margin:0cm 0cm 0pt;text-indent:28pt;mso-char-indent-count:2.0;" class="MsoNormal"><span style="font-family:宋体;font-size:14pt;">江志雄，男，毕业于第一军医大学。副主任医师，杭州师范学院医学院副教授。从事综合儿内科<span lang="EN-US">20</span>余年，积累了丰富经验，具备全科知识。擅长儿童哮喘、风湿病、感染性疾病、血液病、营养性疾病及儿童生长发育问题。学术研究：主要方向是消化系统疾病、哮喘的临床治疗和抗感染免疫等方向，发表论文<span lang="EN-US">40</span>余篇。<span lang="EN-US"><!--?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /--><o:p></o:p></span></span></p>', 1, 'upload/2012/03/03/20120303043315654.jpg', '', '网络', 'a@ximalaya.com', 1),
