INSERT INTO `gtwebsite_service` (`id`, `parent_id`, `name`, `definition`, `describe`, `sort`, `process`, `price`, `place`, `picture`, `state`, `num`) VALUES
(51, 5, '孕妇护理指导', '妇产医师坐堂，通过面对面提供体重、血压、体温、腹围、心肺听诊、听胎心、孕期B超和各种孕检问题解答及技术指导。让每个孕妈妈可以享受到在医院门诊无法得到的私属健康指导咨询。服务方式：面对面、仪器检查十咨询指导', '<p class="MsoNormal" style="line-height:200%;"><span style="font-family:''宋体'';">妇产医师坐堂，通过面对面提供体重、血压、体温、腹围、心肺听诊、听胎心、孕期</span><span lang="en-us" xml:lang="en-us">B</span><span style="font-family:''宋体'';">超和各种孕检问题解答及技术指导。让每个孕妈妈可以享受到在医院门诊无法得到的私属健康指导咨询。</span></p><p class="MsoNormal" style="line-height:200%;"><span style="font-family:''宋体'';">服务方式：面对面、仪器检查十咨询指导</span></p>', 2, '', 100, '', 'upload/2012/01/13/20120113064846380.png', 1, 50),
(54, 5, '健康咨询', '孕妈妈和0—3岁宝宝的各种健康问题可以通过网络和电话向专家咨询、解答疑难的优质服务，让妈妈可以足不出户就可以享受到专家的健康咨询。', '<div>孕妈妈和0—3岁宝宝的各种健康问题可以通过网络和电话向专家咨询、解答疑难的优质服务，让妈妈可以足不出户就可以享受到专家的健康咨询。</div><div>服务方式：网络和电话</div><div><br /></div>', 3, '', 100, '', 'upload/2012/01/13/20120113074959965.jpg', 1, 50),
(53, 5, '营养配餐指导', '营养师根据每个孕妈咪的健康、体质和胎儿发育状况，为孕妈妈提供孕期、月子、哺乳期的营养配餐指导。', '营养师根据每个孕妈咪的健康、体质和胎儿发育状况，为孕妈妈提供孕期、月子、哺乳期的营养配餐指导。', 1, '', 100, '', 'upload/2012/01/13/20120113074503418.jpg', 1, 50),
(68, 7, '活力吸氧', '专家指导吸氧，增强孕妈妈活力，促进胎儿健康发育。', '', 20, '', 100, '', 'upload/2012/01/13/20120113080416833.jpg', 1, 50),
(71, 7, '心灵分享', '心理专家引导孕妈妈小组分享、经验交流、情绪调整，让快乐成为孕妈妈的生活主旋律。', '', 21, '', 100, '', 'upload/2012/01/13/20120113080605614.jpg', 1, 50),
(73, 7, '拉伢胎教', '以音乐与美术胎教课程构建孕妈妈优质的情绪心理环境，以阅读与语言胎教课程帮助孕妈妈体验美好的孕育文化，为优质宝宝的教育奠定基础。', '', 22, '', 100, '', 'upload/2012/01/13/20120113081157220.jpg', 1, 50),
(105, 10, '禧妈自主餐厅', '禧妈自主餐厅', '<div>孕妈咪一般会存在挑食、多餐和在家缺少专人饮食照料，禧妈拉伢俱乐部将根据这一特点，潜心研究专为孕妇提供饮食服务，让孕妇吃得欢心，吃出健康。</div><div><br /></div><div>1、禧妈煲汤                                      2、禧妈简餐 </div><div>3、禧妈小吃                                      4、禧爸套餐</div><div>5、鲜果拼盘                                      6、饮品茶水 </div><div><br /></div><div><br /></div><div>服务方式：到堂点单</div><div><br /></div>', 24, '', 0, '', 'upload/2012/01/13/20120113092007990.jpg', 1, 30),
(101, 6, '技能练习课', '技能练习课', '<div><a href="/index.php/site/column/id/89/news_id/210">拉美兹呼吸减痛分娩</a>、<a href="/index.php/site/column/id/89/news_id/213">母乳喂养与技巧</a>、<a href="/index.php/site/column/id/89/news_id/215">婴胎儿抚触与按摩</a>、<a href="/index.php/site/column/id/89/news_id/218">迎接可爱小天使</a>（演练新生儿护理的方法）、准爸爸训练营、<a href="/index.php/site/column/id/89/news_id/224">婴儿沐浴</a>、<a href="/index.php/site/column/id/89/news_id/226">孕期危机情况应对训练</a>。</div><div>服务方式：专家授课，个性化互动式培训</div>', 3, '', 0, '', 'upload/2012/01/13/20120113091548408.jpg', 1, 50),
(104, 9, '禧妈DIY沙龙', '禧妈DIY沙龙', '<div>定期组织专业多彩的沙龙活动，丰富每位孕妈妈的孕期生活，并通过各种DIY活动，让孕妈妈、准爸爸们成为多才多艺的时尚生活能手，让每个家庭其乐融融，创建和谐的家庭氛围，为宝宝创造一个美好的家！</div><div><br /></div><div>1、学煲营养汤                                2、精美点心制作</div><p>3、艺术插花                                    4、肚皮彩绘</p><p>5、手工艺DIY                                   6、家居装饰</p><p> </p><div>服务方式：沙龙活动，技艺传授，户外活动，自主交流</div><div><br /></div>', 23, '', 288, '', 'upload/2012/01/13/20120113091840287.jpg', 1, 50),
(100, 6, '理论知识课', '理论知识课', '<p>课程内容：</p><p>1）孕期保健知识及生活指导——为爱而孕、<a href="/index.php/site/column/id/89/news_id/209">孕期营养与健康</a>、<a href="/index.php/site/column/id/89/news_id/211">胎儿不同阶段的发育状况</a>、<a href="/index.php/site/column/id/89/news_id/217">预防出生缺陷</a>、<a href="/index.php/site/column/id/89/news_id/222">孕期疾病预防</a>、<a href="/index.php/site/column/id/89/news_id/249">孕期不适的自我调节</a>、<a href="/index.php/site/column/id/89/news_id/227">全孕期产检与保健</a>、<a href="/index.php/site/column/id/89/news_id/239">孕晚期安全与胎儿</a>、<a href="/index.php/site/column/id/89/news_id/241">分娩计划与方法选择</a>、<a href="/index.php/site/column/id/89/news_id/242">母婴用品准备</a>、<a href="/index.php/site/column/id/89/news_id/243">如何选择月嫂</a>等。</p><p>2）早期教育的理念及方法——<a href="/index.php/site/column/id/89/news_id/244">科学月子护理</a>、<a href="/index.php/site/column/id/89/news_id/245">婴幼儿营养指导</a>、<a href="/index.php/site/column/id/89/news_id/246">婴幼儿意外伤害处理</a>、<a href="/index.php/site/column/id/89/news_id/247">0-3岁婴幼儿智能开发以及适合的玩具</a>、<a href="/index.php/site/column/id/89/news_id/248">游戏</a>等。</p><p>服务方式：自助式视频点播（邀请著名专家讲课后做成视频课件）</p><div><br /></div>', 3, '', 0, '', 'upload/2012/01/13/20120113091236265.jpg', 1, 50),
(102, 8, '孕期运动', '孕期运动', '<div>通过运动增强孕妈妈健康，调整身体最佳状态，帮助孕妈妈自然、快乐地分娩。</div><div>A、孕期瑜伽</div><div>B、孕妇操</div><div>C、月子瑜伽</div><div>服务方式：瑜伽名师在医生指导下到馆锻炼。</div><div><br /></div>', 22, '', 0, '', 'upload/2012/01/13/20120113091730265.jpg', 1, 50),
(103, 8, '魅力禧妈', '魅力禧妈', '<div>   帮助产后妈妈进行有效的身形恢复，让妈妈更健康、美丽、自信地生活，做一个极具魅力的完美妈妈。</div><div>   A、哺乳期瑜伽</div><div>   B、哺乳期后瑜伽</div><div>   C、韵舞（操）</div><div>服务方式：瑜伽名师在医生指导下到馆锻炼。</div>', 22, '', 0, '', 'upload/2012/01/13/20120113091758437.jpg', 1, 50);
